<script>
document.addEventListener('mousemove', (e) => {
  const rect = document.getElementById('bot-avatar').getBoundingClientRect();
  const cx = rect.left + rect.width / 2;
  const cy = rect.top + rect.height / 2;
  const dx = e.clientX - cx;
  const dy = e.clientY - cy;
  const angle = Math.atan2(dy, dx);
  const max = 2.5;
  const ox = Math.cos(angle) * max;
  const oy = Math.sin(angle) * max;
  document.getElementById('pupil-left').setAttribute('cx', 40 + ox);
  document.getElementById('pupil-left').setAttribute('cy', 45 + oy);
  document.getElementById('pupil-right').setAttribute('cx', 60 + ox);
  document.getElementById('pupil-right').setAttribute('cy', 45 + oy);
});

document.getElementById('chat-form').onsubmit = async (e) => {
  e.preventDefault();
  const input = document.getElementById('user-msg');
  const msg = input.value.trim();
  if (!msg) return;

  const messagesDiv = document.getElementById('chat-messages');
  messagesDiv.innerHTML += `<div class="msg-user"><strong><?= htmlspecialchars($_SESSION['cliente']) ?>:</strong> ${msg}</div>`;
  input.value = '';

  const res = await fetch('Loopy/chat.php', {
    method: 'POST',
    headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
    body: 'mensaje=' + encodeURIComponent(msg)
  });
  const reply = await res.text();

  messagesDiv.innerHTML += `<div class="msg-bot"><strong>L∞py:</strong> ${reply}</div>`;
  messagesDiv.scrollTop = messagesDiv.scrollHeight;

  document.getElementById('bot-face').setAttribute('fill', '#66bb6a');
  setTimeout(() => {
    document.getElementById('bot-face').setAttribute('fill', '#4CAF50');
  }, 1000);
};

/*Historial*/
async function loadHistorial() {
  const res = await fetch('Loopy/fetch.php');
  const data = await res.text();
  document.getElementById('chat-messages').innerHTML = data;
  document.getElementById('chat-messages').scrollTop = document.getElementById('chat-messages').scrollHeight;
}

loadHistorial();



function enviarMensaje(texto) {
    const input = document.getElementById('user-msg');
    input.value = texto;

    document.getElementById('chat-form').dispatchEvent(new Event('submit'));
  }
</script>
